import React, { useEffect, useRef, useState } from 'react';
import './App.css';
import alanBtn from "@alan-ai/alan-sdk-web";
// import { useNavigate } from "react-router-dom";
import Section1Image from "./Images/1.jpg";
import Section2Image from "./Images/2.jpg";
import Section3Image from "./Images/3.jpg";
import Section4Image from "./Images/4.jpg";

function App() {
  const playNow = () => {
    window.btnInstance.playText("Hi there, I am Alan");
  };
  const [activeSection, setActiveSection] = useState(3);

  useEffect(() => {
    if (typeof window !== "undefined") {
      window.btnInstance = alanBtn({
        key: '4d292cf043b0a2ea4d0bf347580da6fd2e956eca572e1d8b807a3e2338fdd0dc/prod',
        onCommand: (commandData) => {
          if (commandData.command === 'command-example') {
              playNow();
          }
        },
      });
    }
  }, []);

  return (
    <div className="App">
        {activeSection === 1 && (
          <div className="App-header"
            style={{  
              backgroundImage: `url(${Section1Image})`,
              backgroundPosition: 'center',
              backgroundSize: 'cover',
              backgroundRepeat: 'no-repeat'
            }}
            onClick={()=>setActiveSection(2)}
          >
          </div>
        )}
        {activeSection === 2 && (
          <div className="App-header"
            style={{  
              backgroundImage: `url(${Section2Image})`,
              backgroundPosition: 'center',
              backgroundSize: 'cover',
              backgroundRepeat: 'no-repeat'
            }}>
          </div>
        )}
        {activeSection === 3 && (
          <div className="App-header"
            style={{  
              backgroundImage: `url(${Section3Image})`,
              backgroundPosition: 'center',
              backgroundSize: 'cover',
              backgroundRepeat: 'no-repeat'
            }}>
          </div>
        )}
        {activeSection === 4 && (
          <div className="App-header"
            style={{  
              backgroundImage: `url(${Section4Image})`,
              backgroundPosition: 'center',
              // backgroundSize: 'cover',
              backgroundRepeat: 'no-repeat',
              width: '100vw',
              height: '90vh'
            }}>
          </div>
        )}
    </div>
  );
}

export default App;
