import React, { useEffect, useRef } from 'react';
import './App.css';
import alanBtn from "@alan-ai/alan-sdk-web";
// import { useNavigate } from "react-router-dom";

function App() {
  const alanBtnContainer = useRef({}).current;
  const logoEl = useRef();
  // const navigate = useNavigate();
  // const alanBtnRef = useRef({}).current;

  useEffect(() => {
    alanBtnContainer.btnInstance = alanBtn({
      key: '4d292cf043b0a2ea4d0bf347580da6fd2e956eca572e1d8b807a3e2338fdd0dc/prod',
      rootEl: alanBtnContainer,
      onCommand: (commandData) => {
        console.log(commandData);
        if (commandData.command === 'command-example') {
          // if (logoEl.current) {
            alanBtnContainer.btnInstance.playText("Hi there, I am Alan");
            // navigate('https://kukufm.com/show/doglapan-raw');
              // logoEl.current.style.transform = 'rotate(180deg)';
          // }
        }
      }
    });
  }, []);

  return <div className="App">
    <header className="App-header">
      <img src="https://alan.app/previews/preview.png"
        ref={logoEl}
        className="Alan-logo" alt="logo" />
      <ul>
        <li>Say: "Hi!"</li>
        <li>Say: "Execute command"</li>
      </ul>
    </header>
    <div ref={alanBtnContainer}></div>
  </div>;
}

export default App;
