import React, { useEffect, useRef } from 'react';
import './App.css';
import alanBtn from "@alan-ai/alan-sdk-web";
// import { useNavigate } from "react-router-dom";

function App() {
  const alanBtnContainer = useRef({}).current;
  const logoEl = useRef();
  // const navigate = useNavigate();
  // const alanBtnRef = useRef({}).current;
  const playNow = () => {
    window.btnInstance.playText("Hi there, I am Alan");
  };

  const alanBtnRef = useRef({}).current;
  useEffect(() => {
    window.btnInstance = alanBtn({
      key: '4d292cf043b0a2ea4d0bf347580da6fd2e956eca572e1d8b807a3e2338fdd0dc/prod',
      onCommand: (commandData) => {
        if (commandData.command === 'command-example') {
            playNow();
          // Call client code that will react to the received command
        }
      },
    });
  }, []);

  // useEffect(() => {
