import React, { useEffect, useRef, useState } from 'react';
import './App.css';
import alanBtn from "@alan-ai/alan-sdk-web";
// import { useNavigate } from "react-router-dom";
import Section1Immager from "../public/1.jpg";

function App() {
  // const alanBtnContainer = useRef();
  const logoEl = useRef();
  const playNow = () => {
    window.btnInstance.playText("Hi there, I am Alan");
  };
  const [activeSection, setActiveSection] = useState(1);

  useEffect(() => {
    if (typeof window !== "undefined") {
      window.btnInstance = alanBtn({
        key: '4d292cf043b0a2ea4d0bf347580da6fd2e956eca572e1d8b807a3e2338fdd0dc/prod',
        onCommand: (commandData) => {
          if (commandData.command === 'command-example') {
              playNow();
          }
        },
      });
    }
  }, []);

  return (
    <div className="App">
      <header className="App-header">
        <img src=''
          ref={logoEl}
          className="Alan-logo" alt="logo" />
        <ul>
          <li>Say: "Execute command"</li>
        </ul>
      </header>
      {/* <div ref={alanBtnContainer}></div> */}
      {activeSection === 1 && (
        <div
          style={{  
            backgroundImage: "url(" + "https://images.pexels.com/photos/34153/pexels-photo.jpg?auto=compress&cs=tinysrgb&h=350" + ")",
            backgroundPosition: 'center',
            backgroundSize: 'cover',
            backgroundRepeat: 'no-repeat'
          }}>
        </div>
      )}
    </div>
  );
}

export default App;
